import React, { useState, useEffect } from 'react';
import ReactHtmlParser from 'react-html-parser';
// import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import { TrixEditor } from "react-trix";
import docsModel from '../models/docs';


const Editor = () => {

    const [docs, setDocs] = useState([]);
    const [currentDoc, setCurrentDoc] = useState({});

    useEffect(() => {
        (async () => {
            const allDocs = await docsModel.getAllDocs();
            setDocs(allDocs);
        })();
    }, [currentDoc]);

    let fetchDoc = (e, editor) => {
        console.log("hämta valt dokument och visa det i editorn");
        var docId = document.getElementById("selectDoc").value;
        console.log(docId);
        console.log(docs[docId]);
        console.log(docs[docId]._id);
        console.log(docs[docId].name);
        console.log(docs[docId].content);
        let content = docs[docId].content;
        // vet vilket id valt dokument har
        // kontakta databasmodellen med det id:t och gör en fetch get
        // returnera innehållet i en variabel och visa upp i editorn

        //eller har redan doc?
        // let fetchdoc = (doc) => {
            // setData(doc);
        // }
        let x = document.getElementById("trixEditorContent");
        console.log(x);
        // x.innerHTML = content; 
        return; 
    }


    const [data, setData] = useState(''); // Från början är data '' (?) // Första elementet av denna komponents state är nuvarande state, andra funktionen som uppdaterar tillståndet. Från början är tillståndet '' - ingen text

    const printData = () => { 
        console.log("skriv ut i konsollen");
        if (ReactHtmlParser(data)[0].props !== undefined) {
            console.log( ReactHtmlParser(data)[0].props.children ); // ger en array med objekt i
        }
        // for (let row in  ReactHtmlParser(data)[0].props.children[row]) {
            // \filtrera ut element (br mm ?
        //     console.log(row);
        // }
    };

    const createObject = () => {
        // kontakta modellen
        // skicka in data till modellen och låt en göra en insertOne
        console.log( "skapa objekt" );
    };

    const updateObject = () => { 
        console.log( "uppdatera objekt" );
    };

    const saveObject = () => { 
        console.log( "spara objekt" );
    };

    // function handleChange(e, editor) {
    let handleChange = (e, editor) => { 
        // setData(editor.getData());
        setData(e+e);
    }

    let handleEditor = (e, editor) => { 
        // setData(editor.getData());
        setData(e+e);
    }

    return (
        <div className="editor">
            <h1>Text editor</h1>

            {/* Det här är bara en knapp, vi ska använda ett toolbar-element (?) */}
            {/* <button onClick = {()=> printData() }> Save </button> */}
            <trix-toolbar id="trix-toolbar">
                <select id = "selectDoc" onChange={fetchDoc}>
                    {/* console.log(docs); */}
                    <option value="-99" key="0">Choose a document</option>
                    {docs.map((doc, index) => <option value={index} key={index}>{doc.name}</option>)}
                </select>
                <button onClick = {()=> createObject() }> Create </button>
                <button onClick = {()=> updateObject() }> Update </button>
                <button onClick = {()=> printData() }> Save </button>
                {/* <button onClick = {()=> saveObject() }> Save </button> Gör detta efter kmom01 när datan bara ska skrivas ut*/} 
            </trix-toolbar>

            {/* <trix-editor input="input_id" toolbar="trix-toolbar"></trix-editor>
            <input type="hidden" id="input_id" /> */}

            <TrixEditor id = "trixEditorContent" className="trix-editor" toolbar="trix-toolbar"
                // onChange={this.handleChange}
                // onEditorReady={this.handleEditorReady}
                onEditorReady={(e, editor) => { handleEditor(e, editor) }}
                onChange = {(e, editor) => { handleChange(e, editor) }}
                />
        </div>
    )
}

